mapper.select:


     and e.student_id = #{studentId}
     and e.open_id = #{openId}
     and e.overall_score = #{overallScore}

order by e.eval_time desc

mapper.update:
update edu_course_evaluation
teaching_score = #{teachingScore}, content_score = #{contentScore}, difficulty_score = #{difficultyScore}, workload_score = #{workloadScore}, overall_score = #{overallScore}, comment = #{comment}, is_anonymous = #{isAnonymous}, update_time = #{updateTime},
where eval_id = #{evalId}
