mapper.select:


     and o.term_id = #{termId}
     and o.course_id = #{courseId}
     and c.course_name like concat('%', #{courseName}, '%')
     and o.teacher_id = #{teacherId}
     and u.nick_name like concat('%', #{teacherName}, '%')
     and o.status = #{status}

order by o.open_id desc

mapper.update:
update edu_course_opening
term_id = #{termId}, course_id = #{courseId}, teacher_id = #{teacherId}, class_time = #{classTime}, class_location = #{classLocation}, max_students = #{maxStudents}, selected_num = #{selectedNum}, status = #{status}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where open_id = #{openId}
