mapper.select:


     and sc.student_id = #{studentId}
     and su.student_id like concat('%', #{studentNo}, '%')
     and su.nick_name like concat('%', #{studentName}, '%')
     and sc.open_id = #{openId}
     and c.course_name like concat('%', #{courseName}, '%')
     and sc.status = #{status}

order by sc.sc_id desc


where sc.student_id = #{studentId}
  and sc.score is not null

  and o.term_id = #{termId}


  and sc.score >= 60


  and sc.score < 60

order by o.term_id desc, sc.sc_id desc

mapper.update:
update edu_student_course
score = #{score}, usual_score = #{usualScore}, exam_score = #{examScore}, status = #{status}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where sc_id = #{scId}
