mapper.select:


     and o.term_id = #{termId}
     and o.course_id = #{courseId}
     and c.course_name like concat('%', #{courseName}, '%')
     and o.teacher_id = #{teacherId}
     and u.nick_name like concat('%', #{teacherName}, '%')
     and o.status = #{status}

order by o.open_id desc

mapper.update:
update edu_course_opening
term_id = #{termId}, course_id = #{courseId}, teacher_id = #{teacherId}, class_time = #{classTime}, class_location = #{classLocation}, max_students = #{maxStudents}, selected_num = #{selectedNum}, status = #{status}, score_deadline = #{scoreDeadline}, usual_ratio = #{usualRatio}, exam_ratio = #{examRatio}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where open_id = #{openId}

mapper.select:
select o.open_id, o.term_id, o.course_id, o.teacher_id, o.class_time, o.class_location, o.max_students, (select count(*) from edu_student_course sc where sc.open_id = o.open_id and sc.status != '2') as selected_num, o.status, o.score_deadline, o.create_by, o.create_time, o.update_by, o.update_time, o.remark, t.term_name, c.course_code, c.course_name, c.credit, u.nick_name as teacher_name, (select count(*) from edu_student_course sc where sc.open_id = o.open_id and sc.score is null and sc.status = '0') as pending_count from edu_course_opening o left join edu_term t on o.term_id = t.term_id left join edu_course c on o.course_id = c.course_id left join edu_teacher et on o.teacher_id = et.teacher_id left join sys_user u on et.user_id = u.user_id
and o.teacher_id = #{teacherId} and o.term_id = #{termId} and o.status = #{status}
order by o.term_id desc, o.open_id desc

mapper.update:
update edu_course_opening 
set status = '1', is_finished = '1', finish_time = now(), update_time = now()
where open_id = #{openId}

update edu_course_opening 
set status = '0', is_finished = '0', finish_time = null, update_time = now()
where open_id = #{openId}
